javascript:(function(){let body=document.querySelector("body");body.style.visibility="hidden",body.style.overflow="hidden";let wrapper=document.querySelector("#main_board_wrapper");wrapper.style.visibility="visible",wrapper.style.position="fixed",wrapper.style.top="0",wrapper.style.left="0",wrapper.style.margin="0",wrapper.style.padding="0",wrapper.style.width="100vw",wrapper.style.height="100vh",wrapper.style.display="flex",wrapper.style.justifyContent="center",wrapper.style.alignItems="center";let board=document.querySelector("#main_board"),goals=document.querySelector("#goals");goals.style.display="none";let scaleTheBoard=()=>{board.style.transform="scale(1)";let{width:e,height:t}=board.getBoundingClientRect(),{width:r,height:l}=wrapper.getBoundingClientRect(),a=Math.min(r/e,l/t);board.style.transform=`scale(${a})`};scaleTheBoard(),window.onresize=scaleTheBoard;}());